CREATE TABLE `cbt_center`.`student_registration` (`Id` INT(8) NOT NULL AUTO_INCREMENT , `Photo_Id` BLOB NOT NULL , `Registration_Number` VARCHAR(256) NOT NULL , `Surname` VARCHAR(100) NOT NULL , `First_Name` VARCHAR(100) NOT NULL , `Other_Name` VARCHAR(100) NOT NULL , `Gender` VARCHAR(8) NOT NULL , `DOB` DATE NOT NULL , `State_of_Origin` VARCHAR(100) NOT NULL , `Joined_Date` DATE NOT NULL , `Parent_Guardian_Name` VARCHAR(256) NOT NULL , `Parent_Guardian_Email` VARCHAR(100) NOT NULL , `Parent_Guardian_Number` VARCHAR(24) NOT NULL , `Parent_Guardian_Address` VARCHAR(256) NOT NULL , `Current_Class` VARCHAR(24) NOT NULL , PRIMARY KEY (`Id`)) ENGINE = InnoDB;
